                </div>
                <div className='product-rating'>
                {product.rating} Stars
                </div>
                <div className='num-reviews'>
                {product.numreviews} Reviews
                </div>

                
            </div>
            </li>
           
        
            )}
           
            </div>
        
    
    );

}

export default HomeScreen;
