import { BrowserRouter, Link, Route } from "react-router-dom";
import { useSelector } from "react-redux";
import "./App.css";
import CartScreen from "./screens/CartScreen";
import HomeScreen from "./screens/HomeScreen";
import ProductScreen from "./screens/ProductScreen";
import ProductsScreen from "./screens/ProductsScreen";
import SignInScreen from "./screens/SignInScreen";
import SignUpScreen from "./screens/SignUpScreen";
import Cookie from "js-cookie";
function App() {
  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo } = userSignin;
  const userSignup = useSelector((state) => state.userSignup);
  const { newuserInfo } = userSignup;
  const opensidebar = () => {
    document.querySelector(".sidebar").classList.add("open");
  };
  const closesidebar = () => {
    document.querySelector(".sidebar").classList.remove("open");
  };
  const logoutHandler = () => {
    if (userInfo) {
      Cookie.remove("userInfo");
      window.location.reload();
    } else {
      Cookie.remove("newuserInfo");
      window.location.reload();
    }
  };

  return (
    <BrowserRouter>
      <header className="header">
        <button className="sidebar-button" onClick={opensidebar}>
          &#9776;
        </button>
        <Link to="/" className="brand">
          shopEt
        </Link>
        <a href="/usercart" className="cart">
          Cart
        </a>
        {userInfo ? (
          <div className="profile">
            <button className="signin">{userInfo.name}</button>
            <div className="profilelinks">
             
                <Link to="/profile" className="profilebutton">
                  Profile
                </Link>
              
              <button className="logoutbutton" onClick={logoutHandler}>
                Logout
              </button>
            </div>
          </div>
        ) : newuserInfo ? (
          <div className="profile">
            <button className="signin">{newuserInfo.name}</button>
            <div className="profilelinks">
              <Link to="/profile" className="profilebutton">
                Profile
              </Link>
              <button className="logoutbutton" onClick={logoutHandler}>
                Logout
              </button>
            </div>
          </div>
        ) : (
          <Link to="/signin" className="signin">
            Sign In
          </Link>
        )}
      </header>
      <div className="sidebar">
        <h3>Shopping Categories</h3>
        <button className="sidebar-close-button" onClick={closesidebar}>
          &#8592;
        </button>
        <ul className="categories">
          <li>
            <Link to="/category/Pants">Pants</Link>
          </li>

          <li>
            <Link to="/category/Shirts">Shirts</Link>
          </li>
        </ul>
      </div>

      <main className="main">
        <Route path="/" exact={true} component={HomeScreen} />
        <Route path="/signin" component={SignInScreen} />
        <Route path="/signup" component={SignUpScreen} />
        <Route path="/products" component={ProductsScreen} />
        <Route path="/products/:id" component={ProductScreen} />
        <Route path="/cart/:id?" component={CartScreen} />
        <Route path="/usercart" component={CartScreen} />
      </main>

      <footer className="footer">All Rights Reserved.</footer>
    </BrowserRouter>
  );
}

export default App;
