import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { signin } from '../actions/userActions';
import { saveProduct, listProducts } from '../actions/ProductActions';

function ProductsScreen(props) {
  const [modalVisible, setModalVisible] = useState(false);
  const [id, setId] = useState('');
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('');
  const [countInStock, setCountInStock] = useState('');
  const [description, setDescription] = useState('');
  const productList = useSelector(state => state.productList);
  const { loading, products, error } = productList;

  const productSave = useSelector(state => state.productSave);
  const { loading: loadingSave, success: successSave, error: errorSave } = productSave;

  //const productDelete = useSelector(state => state.productDelete);
  //const { loading: loadingDelete, success: successDelete, error: errorDelete } = productDelete;
  const dispatch = useDispatch();

//   useEffect(() => {
//     if (successSave) {
//       setModalVisible(false);
//     }
//     dispatch(listProducts());
//     return () => {
//       //
//     };
//   }, [successSave, //successDelete
// ]);

//   const openModal = (product) => {
//     setModalVisible(true);
//     setId(product._id);
//     setName(product.name);
//     setPrice(product.price);
//     setDescription(product.description);
//     setImage(product.image);
//     setBrand(product.brand);
//     setCategory(product.category);
//     setCountInStock(product.countInStock);
//   }
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(saveProduct({
      _id: id,
      name, price, image, brand, category,
      countInStock, description
    }));
  }
//   const deleteHandler = (product) => {
//     dispatch(deleteProdcut(product._id));
//   }
  return <div className="createproduct_container">

    <div className="signintitle">
      <h3>Create Product</h3>
     
    </div>
    
      
        <form onSubmit={submitHandler} >
          <ul className="form-container">
            <li>
              {loadingSave && <div>Loading...</div>}
              {errorSave && <div>{errorSave}</div>}
            </li>

            <li>
              <label htmlFor="name">
                Name
          </label>
              <input type="text" name="name" className="name1" value={name} id="name" onChange={(e) => setName(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="price">
                Price
          </label>
              <input type="text" name="price" className="name1" value={price} id="price" onChange={(e) => setPrice(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="image">
                Image
          </label>
              <input type="text" name="image" className="name1" value={image} id="image" onChange={(e) => setImage(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="brand">
                Brand
          </label>
              <input type="text" name="brand" className="name1" value={brand} id="brand" onChange={(e) => setBrand(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="countInStock">
                CountInStock
          </label>
              <input type="text" name="countInStock" className="name1" value={countInStock} id="countInStock" onChange={(e) => setCountInStock(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="name">
                Category
          </label>
              <input type="text" name="category" className="name1" value={category} id="category" onChange={(e) => setCategory(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="description">
                Description
          </label>
              <textarea name="description" value={description} className="name1" id="description" onChange={(e) => setDescription(e.target.value)}></textarea>
            </li>
            <li>
              <button type="submit" className="button primary">{id ? "Update" : "Create"}</button>
            </li>
            <li>
              <button type="button" onClick={() => setModalVisible(false)} className="button secondary">Back</button>
            </li>
          </ul>
        </form>
      </div>



        
    
}
export default ProductsScreen;
