import React from "react";
import { productDetailsReducer } from "../reducers/ProductReducer";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {detailsProduct, productDetails} from '../actions/ProductActions';

function ProductScreeen(props) {
  // const product = data.products.find(x => x._id === props.match.params.id);

  // console.log(props.match.params.id)

  const productDetails = useSelector((state) => state.productDetails);
  const productId = props.match.params.id;
  const { loading, product, error } = productDetails;
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(detailsProduct(productId));

    return () => {
      //
    };
  }, []);

  return loading ? (
    <div>Loading..</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <div className="product_details">
      <img
        src={product.image}
        alt="Product img"
        className="product_img"
      ></img>

      <span className="detail">
        <div className="title">{product.title}</div>
        <div className="subtitle">This is the description of the product</div>
        <div className="rating">{product.rating} Stars</div>
        <div className="numreviews">{product.numreviews} Reviews</div>
        <div className="price">â‚¹ {product.price}</div>
        <div className="counter_addtocart">
          <div className="counter">
            Quantity: &nbsp;&nbsp;
            <select className="quantity">
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
            </select>
          </div>
        </div>

        <button className="addtocart">Add to Cart</button>
      </span>
    </div>
  );
}

export default ProductScreeen;
