const cartReducer = (state={cartItems:[]},action) =>
{
    switch(action.type)
    {
        case ADD_ITEM_CART:
            
        const item = action.payload;
        const product = state.cartItems.find(x => x.product === item.product);
        if(product)
        {
            return{
                cartItems:[
                state.cartItems.map(x=>x.product===item.product?item:x)]
            }
           
        }
        return{
            cartItems :[...cartItems, item]
        }
            
    }
}

export { cartReducer };
